import React, { useState } from 'react';
import { useHistory } from 'react-router'; // Import useHistory from React Router
import './login.css';

const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory(); // Access history object from React Router

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            // Make a POST request to your login API endpoint
            const response = await fetch('YOUR_LOGIN_API_ENDPOINT', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            if (!response.ok) {
                // Handle authentication failure (e.g., display error message)
                console.error('Login failed');
                return;
            }

            // Login successful, extract token or session from response
            const { token } = await response.json();

            // Store token or session in browser's local storage
            localStorage.setItem('token', token);

            // Redirect to admin panel route
            history.push('/admin');
        } catch (error) {
            console.error('Error occurred during login:', error);
        }
    };

    return (
        <div className="login-container">
            <img src={require('../photos/winnercoloresvg.svg').default} alt="Description de l'image" className='login-img' />
            <div className="form-container">
                <h2>Connexion Administrateur</h2>
                <form onSubmit={handleSubmit} className='logf'>
                    <input type="text" id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} required placeholder='Username :' />
                    <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required placeholder='Password :'/>
                    <button type="submit" name='submit'>Se connecter</button>
                </form>
            </div>
        </div>
    );
};

export default LoginForm;
