import React, { useState } from "react";
import './regist.css';

const Regist = () => {
    // State variables to store form data
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');
    const [typeAtelier, setTypeAtelier] = useState('drawing'); // Default value
    const [email, setEmail] = useState('');
    const [tel, setTel] = useState('');

    // Function to handle form submission
    const handleSubmit = (event) => {
        event.preventDefault(); // Prevent default form submission behavior

        // Construct the form data
        const formData = new FormData();
        formData.append('nom', nom);
        formData.append('prenom', prenom);
        formData.append('type_atelier', typeAtelier);
        formData.append('email', email);
        formData.append('tel', tel);

        // Make a POST request to the PHP script
        fetch('http://localhost/winner1/insert_registration.php', {
            method: 'POST',
            body: formData
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.text();
        })
        .then(data => {
            // Handle successful registration
            console.log(data); // Log the response from the PHP script
            alert('Registration successful'); // Display success message
        })
        .catch(error => {
            // Handle errors
            console.error('Error:', error);
            alert('Registration failed'); // Display error message
        });
    };

    return (
        <div className="registrationForm">
            <img src={require('../photos/winnercoloresvg.svg').default} alt="Description de l'image" />
            <div className="formulairee">
                <form onSubmit={handleSubmit}>
                        <input type="text" name="nom" placeholder="Nom :" value={nom} onChange={(e) => setNom(e.target.value)} />
                    
                    <label>
                        Prenom:
                        <input type="text" name="prenom" value={prenom} onChange={(e) => setPrenom(e.target.value)} />
                    </label>
                    <label>
                        Type d'atelier :
                        <select name="typeAtelier" value={typeAtelier} onChange={(e) => setTypeAtelier(e.target.value)}>
                            <option value="drawing">Dessin</option>
                            <option value="violin">Violon</option>
                            <option value="guitar">Guitare</option>
                            <option value="theater">Théâtre</option>
                            <option value="crochet">Crochet</option>
                            <option value="piano">Piano</option>
                            <option value="photo">Photographie</option>
                            <option value="stylisme">Stylisme</option>
                            <option value="Andalous">Andalous</option>
                            <option value="Couture">Couture</option>
                            <option value="broderie">Broderie</option>
                        </select>
                    </label>
                    <label>
                        Email :
                        <input type="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                    </label>
                    <label>
                        Numero de tel:
                        <input type="tel" name="tel" value={tel} onChange={(e) => setTel(e.target.value)} />
                    </label>
                    <input type="submit" value="S'inscrire" />
                </form>
            </div>
        </div>
    );
}

export default Regist;
