import React from 'react';
import 'cours.css';
const Cours = () => {
    const courses = [
        {    
            titre: 'Cours de violon',
            description: 'Ce cours est conçu pour vous initier aux bases du solfège et du jeu du violon. Vous apprendrez à jouer des notes simples et à progresser vers des techniques plus avancées, comme les arpèges, les trilles, et les glissandi. ',
           prix:'234' ,
           planning:'dimance',
           date:' 24/05/2022',
        Image: ''
        },
        {
            titre: 'Cours de guitare',
            description: 'En cours de guitare, vous apprenez à jouer de la guitare en suivant une progression structurée qui couvre les bases et les techniques avancées de la musique',
           prix:'100' ,
           planning:'lundi',
           date:' 24/05/2022',
           Image: ''
        
        },
        {
            titre: 'Cours de piano',
            description: "Le cours de piano est une excellente introduction à la musique classique et à l' appréciation de la musique. Vous commencerez par apprendre les bases du solfège et les techniques de base du piano, comme les accords, les gammes, et les arpèges.",
           prix:'200' ,
           planning:'Mardi',
           date:'29/04/2024',
           Image: ''
        
        },
        {
            titre: 'Cours de guitare',
            description: 'Apprenez les bases du solfege et du Violon.',
           prix:'100' ,
           planning:'lundi',
           date:' 24/05/2022',
           Image: ''
        
        },
        {
            titre: 'Cours de guitare',
            description: 'Apprenez les bases du solfege et du Violon.',
           prix:'100' ,
           planning:'lundi',
           date:' 24/05/2022',
           Image: ''
        
        },
        {
            titre: 'Cours de guitare',
            description: 'Apprenez les bases du solfege et du Violon.',
           prix:'100' ,
           planning:'lundi',
           date:' 24/05/2022',
           Image: ''
        
        },
        {
            titre: 'Cours de guitare',
            description: 'Apprenez les bases du solfege et du Violon.',
           prix:'100' ,
           planning:'lundi',
           date:' 24/05/2022',
           Image: ''
        
        },
        {
            titre: 'Cours de guitare',
            description: 'Apprenez les bases du solfege et du Violon.',
           prix:'100' ,
           planning:'lundi',
           date:' 24/05/2022',
           Image: ''
        
        },
        {
            titre: 'Cours de guitare',
            description: 'Apprenez les bases du solfege et du Violon.',
           prix:'100' ,
           planning:'lundi',
           date:' 24/05/2022',
           Image: 'src/photos/winnercoloresvg.svg'
        
        },
        
    ];

    const Course = ({ course }) => {
        return (
            <div className='couRs' style={{ backgroundImage: `url(${course.image})`}}>
                <h3>{course.titre}</h3>
                <p>{course.description}</p>
                <span className='logo'></span>
                <span className='prix'>{course.prix}DA </span>
                <span className='date'>{course.date}</span>

                
               
            </div>
        );
    };

    return (
        <section className='courses-container'>
            {courses.map((course, index) => (
                <Course key={index} course={course} />
            ))}
        </section>
    );
};

export default Cours;
