import React, { useState, useEffect } from 'react';
import './ventes.css';

const ProductCard = ({ product }) => {
    return (
        <div className="product-card">
            <img src={process.env.PUBLIC_URL + product.image} alt={product.name} className="product-image" />
            <div className="product-info">
                <h3>{product.titre}</h3>
                <p>{product.description}</p>
                <p>{product.prix} DA</p>
            </div>
        </div>
    );
};

const Ventes = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch('http://localhost/winner1/fetch_products.php')
            .then(response => response.json())
            .then(data => {
                setProducts(data);
            })
            .catch(error => console.error('Error fetching products:', error));
    }, []);

    return (
        <div className="ventes-container">
            {products.length > 0 ? (
                products.map((product, index) => (
                    <ProductCard key={index} product={product} />
                ))
            ) : (
                <h1 className='nop'>Aucun instrument disponible</h1>
            )}
        </div>
    );
};

export default Ventes;
