import React, { useState, useEffect } from 'react';
import "./cours.css";

const Cours = () => {
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        // Fetch courses from backend PHP script
        fetch('http://localhost/winner1/fetch_ateliers.php') // Replace with your backend URL
            .then(response => response.json())
            .then(data => {
                setCourses(data);
            })
            .catch(error => console.error('Error fetching courses:', error));
    }, []);

    const Course = ({ course }) => {
        return (
            <div className='couRs' style={{ backgroundImage: `url(${course.image})`}}>
                <h3>{course.titre}</h3>
                <p>{course.description}</p>
                <span className='logo'></span>
                <span className='prix'>{course.prix}DA </span>
                <span className='date'>{course.date}</span>
            </div>
        );
    };

    return (
        <section className='courses-container'>
            {courses.map((course, index) => (
                <Course key={index} course={course} />
            ))}
        </section>
    );
};

export default Cours;
